def kishi(a,b):
    if a>b:
        return b
    else:
        return a

a,b=map(int,input().split())
print(kishi(a,b))

def bin(n):
    s=''
    while n>0:
        if n%2==0:
            s='0'+s
        else:
            s='1'+s
        n=n//2
    return s

n=int(input())
print(bin(n))
