n=int(input())
a=[int(x) for x in input().split()]
b=max(a)
c=min(a)
for i in range(n):
    if a[i]==b:
        a[i]=c
    elif a[i]==c:
        a[i]=b
    print(a[i],end=' ')

n,k=map(int,input().split())
a=[int(x) for x in input().split()]
for i in range(n):
    if a[i]<k:
        print(a[i]*2)

