n=int(input())
for i in range(1,n+1):
    if n%i==0:
        print(i,end=' ')

n=int(input())
for i in range(1,11):
    print(n,'x',i,'=',n*i)

n=int(input())
a=[]
for i in range(n):
    s=input()
    if len(s)<=6:
        a.append(s)
    else:
        s1=(s[0]+str(len(s)-2))+s[len(s)-1]
        a.append(s1)
for i in a:
    print(i)

k=int(input())
d=(k+2)//3+1
if d%2==0:
    print(d+1)
else:
    print(d)

n,p,k=map(int,input().split())
if n%2==1:
    x=(n+1)//2
else:
    x=n//2
if p%2==1:
    y=(p+1)//2
else:
    y=p//2
if k%2==1:
    z=(k+1)//2
else:
    z=k//2
print(x+y+z)
