def sandar(A):
    if A==[]:
        return 0
    else:
        summ=sandar(A[1:])
        summ=summ+A[0]
        return summ

V=[3,2,7,5,1,9]
summ=sandar(V)
print('summ=',summ)

def GetFibonacciList(n, L):
    count = len(L)
    if len(L)<2:
        return []
    num1 = L[count-2]
    num2 = L[count-1]
    if (num1+num2) < n:
        L = L + [num1+num2]
        return GetFibonacciList(n, L)
    else:
        return L

FB= GetFibonacciList(100, [0, 1])
print('FB = ',*FB)

def greetings(st):
    print(st)
    if len(st) == 0:
        return
    else:
        greetings(st[:-1])

greetings('Salem,alem!')

def darezhe(n, m):
    if m==1:
        return n
    elif m%2==0:
        res=darezhe(n,m//2)
        return res*res
    else:
        res=darezhe(n,m//2)
        return res*res*n

n=int(input())
m=int(input())
print(darezhe(n, m))

def tizbek(n):
    if n < 1:
        return 0
    else:
        return n + tizbek(n - 2)

print(tizbek(int(input())))
