n=int(input())
a=[]
for i in range(n):
    s=input()
    if len(s)<=6:
        a.append(s)
    else:
        s1=(s[0]+str(len(s)-2))+s[len(s)-1]
        a.append(s1)
for i in a:
    print(i)

